const inputFecha = $input.first().json.FECHA;
const inputHora = $input.first().json.HORARIO;

const [anio, mes, dia] = inputFecha.split('T')[0].split('-');
const fechaFormateada = `${dia}/${mes}/${anio.slice(-2)}`;
const horaFormateada = inputHora.slice(0, 5);

const fechaObj = new Date(inputFecha);
const nombreDia = fechaObj.toLocaleDateString('es-ES', { weekday: 'long', timeZone: 'UTC' });
const diaSemana = nombreDia.charAt(0).toUpperCase() + nombreDia.slice(1);

return {
  json: {
    fecha: fechaFormateada,
    hora: horaFormateada,
    dia: diaSemana
  }
};

