// Obtener fecha actual
const hoy = new Date();
const diasSemana = ['Domingo', 'Lunes', 'Martes', 'Mi√©rcoles', 'Jueves', 'Viernes', 'S√°bado'];

// Formatear fecha de hoy
const dia = String(hoy.getDate()).padStart(2, '0');
const mes = String(hoy.getMonth() + 1).padStart(2, '0');
const anio = hoy.getFullYear();

let contexto = `REFERENCIA TEMPORAL:\n- HOY es ${diasSemana[hoy.getDay()]} ${dia}/${mes}/${anio}.\n`;

// Calcular los pr√≥ximos 7 d√≠as para referencia futura
contexto += "REFERENCIA FUTURA (√ösala si el usuario dice 'el pr√≥ximo lunes', etc):\n";

for (let i = 1; i <= 7; i++) {
  const futuro = new Date(hoy);
  futuro.setDate(hoy.getDate() + i);

  const fDia = String(futuro.getDate()).padStart(2, '0');
  const fMes = String(futuro.getMonth() + 1).padStart(2, '0');
  const fAnio = futuro.getFullYear();

  contexto += `- El pr√≥ximo ${diasSemana[futuro.getDay()]} cae ${fDia}/${fMes}/${fAnio}.\n`;
}

